import { HttpService } from './../../../services/http.service';
import { Component } from '@angular/core';
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import {Router} from "@angular/router";
import {ParentModel} from "../../../models/Parent.model";

@Component({
  selector: 'app-list-parents',
  templateUrl: './list-parents.component.html',
  styleUrls: ['./list-parent.component.css']
})
export class ListParentComponent {
  parentFormGroup !: FormGroup;
  parents : ParentModel[]=[];
  parent : any;
  constructor(private httpService : HttpService ,
              private http :HttpService ,
              private fb : FormBuilder,
              private route:Router
  ) {

  }

  ngOnInit(): void {
      this.getParents();
      this.initAddParent();
  }

  getParentById(data : any){
    this.httpService.authGet("parent/"+data).subscribe({
      next : (res: any)=>{
        this.parent=res.body;
        console.log(this.parent);
      }
    })
  }

  delete(id : any){
    this.parents=this.parents.filter((f)=> f.id !== id)
    this.httpService.authDelete("parent/"+id).subscribe({
      next : (data : any)=>{
      }
    })
  }


  getParents(){
    this.httpService.authGet("parent")
      .subscribe({
        next: (data: any) => {

           this.parents= data.body;
           console.log(this.parents)
        },
        error: error => console.log(error)
      });


  }



  initAddParent()
  {
    this.parentFormGroup=this.fb.group({
      nomParent : new FormControl('', [Validators.required ] ),
      prenomParent : new FormControl('', [Validators.required] ),
      cne : new FormControl('',Validators.required ),
      email : new FormControl('',Validators.required ),
      motDePasse : new FormControl('',Validators.required ),
      tel : new FormControl('',Validators.required ),

    });
  }
  save(){
    this.http.post("parent",this.parentFormGroup.value).subscribe(
      (response:any)=>{
        this.route.navigate(["/get-parent"])
        console.log("done ==> ", response)
        this.getParents();
      } , (err : any )=>  {
        console.log(err);

      }
    )
  }
}
<div class="container mt-5 m-5">

    <h2>List des Parents </h2>
</div>
<div class="conainer mt-5 mb-5 m-5">
    <table class="table">
        <caption> Liste des parents </caption>
        <thead>
          <tr>

            <th scope="col">Nom </th>
            <th scope="col">Prenom</th>
            <th scope="col">Cne</th>
            <th scope="col">Email </th>
            <th scope="col">Numero Tel </th>
            <th scope="col">Payement </th>
            <th scope="col">Enfant </th>
            <th scope="col"> Action </th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let parent of parents">
            <td>{{parent.nom}}</td>
            <td>{{parent.prenom}}</td>
            <td>{{parent.cne}}</td>
            <td>{{parent.email}}</td>
            <td>{{parent.tel}}</td>
<!--            <td>{{parent.enfant}}</td>-->
            <td style="font-size: large">
              <span *ngIf="parent.statutPayement==true" style="color: #0bd000"><i class="fa-solid fa-sack-dollar"></i> <i class="fa-sharp fa-solid fa-check"></i></span>
              <span *ngIf="parent.statutPayement==false" style="color: darkred"><i class="fa-solid fa-sack-dollar"></i> <i class="fa-solid fa-xmark"></i></span>
            </td>
            <td><span *ngIf="parent.enfant">{{parent.enfant.nom+" "+parent.enfant.prenom}}</span></td>
            <td>
            <i class="fa-solid fa-trash" (click)="delete(parent.id)" style="color: brown; width: 20px; font-size:large; margin-right: 30px; cursor: pointer;"></i>
            <i class="fa-solid fa-pen-to-square" style="width: 20px; font-size:large;cursor: pointer;"></i>

            </td>
          </tr>

        </tbody>
      </table>

</div>



