import {Component, OnInit} from '@angular/core';
declare var $: any;
@Component({
  selector: 'app-side-bar',
  templateUrl: './side-bar.component.html',
  styleUrls: ['./side-bar.component.css']
})
export class SideBarComponent implements OnInit{

  constructor() { }
  JqueryCode():void{
    // Hide submenus
    $('#body-row .collapse').collapse('hide');

// Collapse/Expand icon
    $('#collapse-icon').addClass('fa-angle-double-left');

// Collapse click
    $('[data-toggle=sidebar-colapse]').click(function() {
      SidebarCollapse();
    });

    function SidebarCollapse () {
      $('.menu-collapsed').toggleClass('d-none');
      $('.sidebar-submenu').toggleClass('d-none');
      $('.submenu-icon').toggleClass('d-none');
      $('#sidebar-container').toggleClass('sidebar-expanded sidebar-collapsed');

      // Treating d-flex/d-none on separators with title
      var SeparatorTitle = $('.sidebar-separator-title');
      if ( SeparatorTitle.hasClass('d-flex') ) {
        SeparatorTitle.removeClass('d-flex');
      } else {
        SeparatorTitle.addClass('d-flex');
      }

      // Collapse/Expand icon
      $('#collapse-icon').toggleClass('fa-angle-double-left fa-angle-double-right');
    }
  }
  ngOnInit(): void {
    this.JqueryCode();
  }
}
<!-- Bootstrap row -->

<div id="sidebar-container" class="sidebar-expanded d-none d-md-block" style="height: 100%">
  <!-- d-* hiddens the Sidebar in smaller devices. Its itens can be kept on the Navbar 'Menu' -->
  <!-- Bootstrap List Group -->
  <ul class="list-group">
    <!-- Separator with title -->
    <li class="list-group-item sidebar-separator-title text-muted d-flex align-items-center menu-collapsed">
      <small>MENU PRINCIPAL</small>
    </li>
    <!-- /END Separator -->

    <a routerLink="/accueil" class="bg-dark list-group-item list-group-item-action">
      <div class="d-flex w-100 justify-content-start align-items-center">
        <span class="fa-solid fa-house mr-3"></span>

        <span class="menu-collapsed">Accueil</span>
      </div>
    </a>

    <li class="list-group-item sidebar-separator-title text-muted d-flex align-items-center menu-collapsed">
      <small>OPTIONS</small>
    </li>
    <!-- /END Separator -->
    <a routerLink="/projets/get-parent" class="bg-dark list-group-item list-group-item-action">
      <div class="d-flex w-100 justify-content-start align-items-center">
        <span class="fa-solid fa-users fa-fw mr-3"></span>
        <span class="menu-collapsed">Parents</span>
      </div>
    </a>
    <a  routerLink="/ressources/liste-ressources" class="bg-dark list-group-item list-group-item-action">
      <div class="d-flex w-100 justify-content-start align-items-center">
        <span class="fa-solid fa-children fa-fw mr-3"></span>
        <span class="menu-collapsed">Ressources</span>
      </div>
    </a>
    <!-- Separator without title -->
    <li class="list-group-item sidebar-separator menu-collapsed"></li>
    <!-- /END Separator -->

    <a data-toggle="sidebar-colapse" class="bg-dark list-group-item list-group-item-action d-flex align-items-center">
      <div class="d-flex w-100 justify-content-start align-items-center">
        <span id="collapse-icon" class="fa fa-2x mr-3"></span>
        <span id="collapse-text" class="menu-collapsed">Fermer</span>
      </div>
    </a>
  </ul><!-- List Group END-->
</div><!-- sidebar-container END -->
