import { Component } from '@angular/core';

@Component({
  selector: 'app-choose-connection',
  templateUrl: './choose-connection.component.html',
  styleUrls: ['./choose-connection.component.css']
})
export class ChooseConnectionComponent {

}
<div class="card-container">
<div class="card" routerLink="/admin-connection">
  <i class="fa-solid fa-briefcase"></i>
  <h5>Espace Administrateur</h5>
  <p>Login</p>
</div>

<div class="nav-link " routerLink="/parent-connection" class="card">
  <i class="fa-solid fa-users"></i>
  <h5>Espace Parent</h5>
  <p>Login</p>
</div>
</div>
