import {Component, OnInit} from '@angular/core';
import {HttpService} from "../../../services/http.service";

@Component({
  selector: 'app-acceuil',
  templateUrl: './acceuil.component.html',
  styleUrls: ['./acceuil.component.css']
})
export class AcceuilComponent implements OnInit{
  nbParents!:number;
  nbEnfants!:number;
  nbAideEduc!:number;
  nbActivités!:number;
  constructor(private httpService:HttpService) {

  }
  ngOnInit(): void {
    this.httpService.authGet("activite")
      .subscribe({
        next: (data: any) => {

          this.nbActivités=data.length;
        },
        error: error => console.log(error)
      });
    this.httpService.authGet("parent")
      .subscribe({
        next: (data: any) => {

          this.nbParents=data.size;
        },
        error: error => console.log(error)
      });
    this.httpService.authGet("enfant")
      .subscribe({
        next: (data: any) => {

          this.nbEnfants=data.size;
        },
        error: error => console.log(error)
      });
    this.httpService.authGet("aideEducateur")
      .subscribe({
        next: (data: any) => {

          this.nbAideEduc=data.size;
        },
        error: error => console.log(error)
      });
  }

}
<div class="cards-container">
<div class="card" routerLink="/admin-connection">
  <i class="fa-solid fa-briefcase"></i>
  <h5>Enfants</h5>
  <p>{{nbEnfants}}</p>
</div>
<div class="card" routerLink="/admin-connection">
  <i class="fa-solid fa-briefcase"></i>
  <h5>Parents</h5>
  <p>{{nbParents}}</p>
</div>
</div>
