import { HttpService } from './../../../services/http.service';
import { Component } from '@angular/core';
import {AddComponent} from "../add-parent/add.component";
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import {Router} from "@angular/router";

@Component({
  selector: 'app-list-parents',
  templateUrl: './list-parents.component.html',
  styleUrls: ['./list-parent.component.css']
})
export class ListParentComponent {
  userFormGroup !: FormGroup;
  parents : any[]=[];
  parent : any;
  constructor(private httpService : HttpService ,
              private http :HttpService ,
              private fb : FormBuilder,
              private route:Router
  ) {

  }

  ngOnInit(): void {
      this.getParents();
      this.initAdd();
  }

  getParentById(data : any){
    this.httpService.authGet("parent/"+data).subscribe({
      next : (res: any)=>{
        this.parent=res.body;
        console.log(this.parent);
      }
    })
  }

  delete(id : any){
    this.parents=this.parents.filter((f)=> f.id !== id)
    this.httpService.authDelete("parent/"+id).subscribe({
      next : (data : any)=>{
      }
    })
  }


  getParents(){
    this.httpService.authGet("parent")
      .subscribe({
        next: (data: any) => {

           this.parents= data.body;
           console.log(this.parents)
        },
        error: error => console.log(error)
      });


  }



  initAdd()
  {
    this.userFormGroup=this.fb.group({
      nom : new FormControl('', [Validators.required ] ),
      prenom : new FormControl('', [Validators.required] ),
      cne : new FormControl('',Validators.required ),
      email : new FormControl('',Validators.required ),
      motDePasse : new FormControl('',Validators.required ),
      tel : new FormControl('',Validators.required ),

    });
  }
  save(){
    this.http.post("parent",this.userFormGroup.value).subscribe(
      (response:any)=>{
        this.route.navigate(["/get-parent"])
        console.log("done ==> ", response)
        this.getParents();
      } , (err : any )=>  {
        console.log(err);

      }
    )
  }
}
<div class="container mt-5 m-5">

    <h2>List des Parents </h2> <button class="btn btn-primary" style="float: right;"
                                       data-bs-toggle="modal" data-bs-target="#myModal"
                                       > Ajouter Parent</button>
</div>
<div class="conainer mt-5 mb-5 m-5">
    <table class="table">
        <caption> Liste des parents </caption>
        <thead>
          <tr>

            <th scope="col">Nom </th>
            <th scope="col">Prenom</th>
            <th scope="col">Cne</th>
            <th scope="col">Email </th>
            <th scope="col">Payement </th>
            <th scope="col"> Action </th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let parent of parents">
            <td>{{parent.nom}}</td>
            <td>{{parent.prenom}}</td>
            <td>{{parent.cne}}</td>
            <td>{{parent.email}}</td>
            <td><span><i class="fa-regular fa-money-check-dollar-pen"></i><i class="fa-duotone fa-check"></i></span>
              <span><i class="fa-regular fa-money-check-dollar-pen"></i><i class="fa-duotone fa-check"></i></span>
            </td>
            <td>
            <i class="fa-solid fa-trash" (click)="delete(parent.id)" style="color: brown; width: 20px; font-size:large; margin-right: 30px; cursor: pointer;"></i>
            <i class="fa-solid fa-pen-to-square" style="width: 20px; font-size:large;cursor: pointer;"></i>

            </td>
          </tr>

        </tbody>
      </table>

</div>



<!-- The Modal -->
<div class="modal fade" id="myModal">
  <div class="modal-dialog">
    <div class="modal-content">

      <!-- Modal Header -->
      <div class="modal-header">
        <h4 class="modal-title">Ajouter un parent</h4>
        <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
      </div>

      <div class="container" style="width: 80%;">
        <form [formGroup]="userFormGroup" >
          <div class="form-group mt-3">
            <label for="nom">Nom </label>
            <input type="text" class="form-control" id="nom"  placeholder="Enter nom" formControlName="nom">
          </div>

          <div class="form-group mt-3">
            <label for="prenom">Prenom </label>
            <input type="text" class="form-control" id="prenom"  placeholder="Enter prenom" formControlName="prenom">
          </div>


          <div class="form-group mt-3">
            <label for="cne"> Cne </label>
            <input type="text" class="form-control" id="cne"  placeholder="Enter cne" formControlName="cne">
          </div>

          <div class="form-group mt-3">
            <label for="email"> Email </label>
            <input type="email" class="form-control" id="email"  placeholder="Enter email" formControlName="email">
          </div>

          <div class="form-group mt-3">
            <label for="motDePasse"> Mot de passe </label>
            <input type="password" class="form-control" id="motDePasse"  placeholder="Enter le mot de  passe" formControlName="motDePasse">
          </div>

        </form>
      </div>


      <!-- Modal footer -->
      <div class="modal-footer" style="margin-top: 20px;display: flex;justify-content: center">
        <button type="button" class="btn btn-primary"  style="width: 80%" (click)="save()" data-bs-dismiss="modal">Save</button>
      </div>

    </div>
  </div>
</div>
