import { HttpService } from './../../services/http.service';
import { Component, OnInit } from '@angular/core';
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";

import { environment } from './../../utile/environement';
@Component({
  selector: 'app-list-enfants',
  templateUrl: './list-enfants.component.html',
  styleUrls: ['./list-enfants.component.css']
})
export class ListEnfantsComponent implements OnInit {
  enfantFormGroup !: FormGroup;
  enfants : any[]=[];
  parentFormGroup !: FormGroup;
  formEnfant:boolean=true;
  constructor(private httpService : HttpService ,
              private fb : FormBuilder,
    ) {

  }

  ngOnInit(): void {
      this.getEnfants();
      this.initAdd();
      this.initAddParent();
  }


  delete(id : any){
    this.enfants=this.enfants.filter((f)=> f.id !== id)
    this.httpService.authDelete("parent/"+id).subscribe({
      next : (data : any)=>{
      }
    })
  }


  getEnfants(){
    this.httpService.authGet("enfant")
      .subscribe({
        next: (data: any) => {

           this.enfants= data.body;
           console.log(this.enfants)
        },
        error: error => console.log(error)
      });
  }
  initAdd()
  {
    this.enfantFormGroup=this.fb.group({
      nomEnfant : new FormControl('', [Validators.required ] ),
      prenomEnfant : new FormControl('', [Validators.required] ),
      age : new FormControl('',Validators.required ),

    })
  }
  save(){
    // this.httpService.post("enfant/ENFANT_NORMAL",this.enfantFormGroup.value).subscribe(
    //   (response:any)=>{
    //     console.log("done ==> ", response)
    //     this.getEnfants();
    //   } , (err : any )=>  {
    //     console.log(err);
    //   }
    // )
    console.log(this.parentFormGroup)
  }


  initAddParent()
  {
    this.parentFormGroup=this.fb.group({
      nomParent : new FormControl('', [Validators.required ] ),
      prenomParent : new FormControl('', [Validators.required] ),
      cne : new FormControl('',Validators.required ),
      email : new FormControl('',Validators.required ),
      motDePasse : new FormControl('',Validators.required ),
      tel : new FormControl('',Validators.required ),

    });
  }
  saveParent(){
    this.httpService.post("parent",this.parentFormGroup.value).subscribe(
      (response:any)=>{
        console.log("done ==> ", response)
      } , (err : any )=>  {
        console.log(err);

      }
    )
  }
}
<div class="container mt-5 m-5">

    <h2>List des Enfants </h2> <button class="btn btn-primary" style="float: right; "
                                       data-bs-toggle="modal" data-bs-target="#myModal"> Ajouter enfant</button>
</div>
<div class="conainer mt-5 mb-5 m-5">
    <table class="table">
        <caption> Liste des enfants </caption>
        <thead>
          <tr>

            <th scope="col">Nom </th>
            <th scope="col">Prenom</th>
            <th scope="col">Age</th>
            <th scope="col">niveau </th>
            <th scope="col"> Action </th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let enfant of enfants">
            <td>{{enfant.nom}}</td>
            <td>{{enfant.prenom}}</td>
            <td>{{enfant.age}}</td>
            <td>{{enfant.niveau}}</td>
            <td>
              <i class="fa-solid fa-trash" (click)="delete(enfant.id)" style="color: brown; width: 20px; font-size:large; margin-right: 30px; cursor: pointer;"></i>
              <i class="fa-solid fa-pen-to-square" style="width: 20px; font-size:large;cursor: pointer;"></i>


            </td>
          </tr>

        </tbody>
      </table>

</div>




<!-- The Modal -->
<div class="modal fade" id="myModal">
  <div class="modal-dialog">
    <div class="modal-content">

      <!-- Modal Header -->
      <div class="modal-header">
        <h4 class="modal-title">Ajouter un enfant</h4>
        <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
      </div>

      <div class="container mr-5 mt-5" style="width: 80%;">
        <form [formGroup]="enfantFormGroup">
          <div [hidden]="formEnfant" >
          <h5>Informations de l'enfant</h5>
          <div class="form-group">
            <label for="nomEnfant">Nom de l'enfant :</label>
            <input type="text" class="form-control" id="nomEnfant"  placeholder="Enter nom" formControlName="nom">
          </div>
          <div class="form-group">
            <label for="prenomEnfant">Prénom de l'enfant :</label>
            <input type="text" class="form-control" id="prenomEnfant" aria-describedby="emailHelp" placeholder="Enter prenom" formControlName="prenom">
          </div>
          <div class="form-group">
            <label for="age">Age de l'enfant :</label>
            <input type="number" class="form-control" id="age" aria-describedby="emailHelp" placeholder="Enter age" formControlName="age">
          </div>



          <div class="form-group">
          <label for="special">Enfant spécial ? </label>
          <select multiple class="form-select" id="special">
            <option>ENFANT AUTISTE</option>
            <option>ENFANT HYPERACTIF</option>
            <option>ENFANT SURDOUE</option>
          </select>
          </div>
          </div>
        </form>
          <form [formGroup]="parentFormGroup" >
            <div [hidden]="!formEnfant" >
              <h5>Informations du parent</h5>
            <div class="form-group mt-3">
              <label for="nomParent">Nom du parent :</label>
              <input type="text" class="form-control" id="nomParent"  placeholder="Nom du parent" formControlName="nom">
            </div>

            <div class="form-group mt-3">
              <label for="prenomParent">Prénom du parent :</label>
              <input type="text" class="form-control" id="prenomParent"  placeholder="Prénom du parent" formControlName="prenom">
            </div>


            <div class="form-group mt-3">
              <label for="cne"> Cne de parent :</label>
              <input type="text" class="form-control" id="cne"  placeholder="Cne de parent" formControlName="cne">
            </div>

            <div class="form-group mt-3">
              <label for="email"> Email du parent :</label>
              <input type="email" class="form-control" id="email"  placeholder="Email du parent" formControlName="email">
            </div>

            <div class="form-group mt-3">
              <label for="motDePasse"> Mot de passe du parent :</label>
              <input type="password" class="form-control" id="motDePasse"  placeholder="Mot de passe du parent" formControlName="motDePasse">
            </div>

            <div class="form-group mt-3">
              <label for="tel"> Numero de telephone du parent :</label>
              <input type="text" class="form-control" id="tel"  placeholder="Numero de telephone du parent" formControlName="motDePasse">
            </div>
            </div>
          </form>
        <div class="next-prev">
          <button type="button" style="width: 40%" class="btn btn-light" (click)="formEnfant=true" ><i class="fa-solid fa-backward"></i> Précédent</button>
          <button type="button" style="width: 40%" class="btn btn-light" (click)="formEnfant=false" >Suivant <i class="fa-solid fa-forward"></i></button>
        </div>
      </div>

      <!-- Modal footer -->
      <div class="modal-footer" style="margin-top: 20px;display: flex;justify-content: center">
        <button type="button" [disabled]="parentFormGroup.status=='INVALID'||!enfantFormGroup.status=='INVALID'" class="btn btn-primary"  style="width: 80%" (click)="save()" data-bs-dismiss="modal">Save</button>
      </div>
    </div>
  </div>
</div>
