import { Router } from '@angular/router';
import { HttpService } from './../../../services/http.service';
import { FormGroup, FormBuilder, FormControl, Validators } from '@angular/forms';
import { Component } from '@angular/core';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css']
})
export class AddComponent {
  userFormGroup !: FormGroup;
  constructor(

    private http :HttpService ,
    private fb : FormBuilder,
    private route:Router
  ){

  }

  ngOnInit(): void {
    this.userFormGroup=this.fb.group({
      nom : new FormControl('', [Validators.required ] ),
      prenom : new FormControl('', [Validators.required] ),
      cne : new FormControl('',Validators.required ),
      email : new FormControl('',Validators.required ),
      motDePasse : new FormControl('',Validators.required ),
      tel : new FormControl('',Validators.required ),

    })

  }

  save(){


    this.http.post("parent",this.userFormGroup.value).subscribe(
      (response:any)=>{
        console.log("done ==> ", response)
      } , (err : any )=>  {
         console.log(err);

      }
    )


    }


}
<div class="container mr-5 mt-5" style="width: 50%;">
      <form [formGroup]="userFormGroup" >
        <div class="form-group mt-3">
          <label for="nom">Nom </label>
          <input type="text" class="form-control" id="nom"  placeholder="Enter nom" formControlName="nom">
        </div>

        <div class="form-group mt-3">
            <label for="prenom">Prenom </label>
            <input type="text" class="form-control" id="prenom"  placeholder="Enter prenom" formControlName="prenom">
          </div>


        <div class="form-group mt-3">
            <label for="cne"> Cne </label>
            <input type="text" class="form-control" id="cne"  placeholder="Enter cne" formControlName="cne">
        </div>

          <div class="form-group mt-3">
            <label for="email"> Email </label>
            <input type="email" class="form-control" id="email"  placeholder="Enter email" formControlName="email">
          </div>

          <div class="form-group mt-3">
            <label for="motDePasse"> Mot de passe </label>
            <input type="password" class="form-control" id="motDePasse"  placeholder="Enter le mot de  passe" formControlName="motDePasse">
          </div>

        <button type="submit" class="btn btn-primary mt-4" (click)="save()"> Ajouter </button>
      </form>
    </div>
