import { HttpService } from './../../../services/http.service';
import { Component } from '@angular/core';
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import {Router} from "@angular/router";

@Component({
  selector: 'app-list-aide-educ',
  templateUrl: './list-aide-educ.component.html',
  styleUrls: ['./list-aide-educ.component.css']
})
export class ListAideEducComponent {
  educateurs : any[]=[];
  userFormGroup !: FormGroup;
  educateur : any;
  constructor(private httpService : HttpService ,
              private http :HttpService ,
              private fb : FormBuilder,
              private route:Router
    ) {

  }

  ngOnInit(): void {
      this.geteducateurs();
      this.initAdd();
  }

  geteducateurById(data : any){
    this.httpService.authGet("aideEducateur/"+data).subscribe({
      next : (res: any)=>{
        this.educateur=res.body;
        console.log(this.educateur);
      }
    })
  }

  delete(id : any){
    this.educateurs=this.educateurs.filter((f)=> f.id !== id)
    this.httpService.authDelete("aideEducateur/"+id).subscribe({
      next : (data : any)=>{
      }
    })
  }


  geteducateurs(){
    this.httpService.authGet("aideEducateur")
      .subscribe({
        next: (data: any) => {

           this.educateurs= data.body;
           console.log(this.educateurs)
        },
        error: error => console.log(error)
      });
  }
  initAdd() {
    this.userFormGroup = this.fb.group({
      nom: new FormControl('', [Validators.required]),
      prenom: new FormControl('', [Validators.required]),
      email: new FormControl('', Validators.required),
      motDePasse: new FormControl('', Validators.required),
      telephone: new FormControl('', Validators.required),
      salaire: new FormControl('', Validators.required),
    });
  }
  save(){
    this.http.post("aideEducateur",this.userFormGroup.value).subscribe(
      (response:any)=>{
        this.geteducateurs();
        console.log("done ==> ", response)
      } , (err : any )=>  {
        console.log(err);
      }
    )
  }

}
<div class="container mt-5 m-5">

    <h2>List des Aide-Educateur </h2> <button class="btn btn-primary"
                                              data-bs-toggle="modal" data-bs-target="#myModal"
                                              style="float: right;" routerLink="/add-parent"> Ajouter Aide-educateur</button>
</div>
<div class="conainer mt-5 mb-5 m-5">
    <table class="table">
        <caption> Liste des aide-educateurs </caption>
        <thead>
          <tr>

            <th scope="col">Nom </th>
            <th scope="col">Prenom</th>
            <th scope="col">Salaire</th>
            <th scope="col">Email </th>
            <th scope="col"> Action </th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let educateur of educateurs">
            <td>{{educateur.nom}}</td>
            <td>{{educateur.prenom}}</td>
            <td>{{educateur.email}}</td>
            <td>{{educateur.salaire}}</td>
            <td>
            <i class="fa-solid fa-trash" (click)="delete(educateur.id)" style="color: brown; width: 20px; font-size:large; margin-right: 30px; cursor: pointer;"></i>
            <i class="fa-solid fa-pen-to-square" style="width: 20px; font-size:large;cursor: pointer;"></i>

            </td>
          </tr>

        </tbody>
      </table>

</div>



<!-- The Modal -->
<div class="modal fade" id="myModal">
  <div class="modal-dialog">
    <div class="modal-content">

      <!-- Modal Header -->
      <div class="modal-header">
        <h4 class="modal-title">Ajouter un parent</h4>
        <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
      </div>

      <div class="container" style="width: 80%;">
        <div class="container mr-5 mt-5">
          <form [formGroup]="userFormGroup" >
            <div class="form-group mt-3">
              <label for="nom">Nom </label>
              <input type="text" class="form-control" id="nom"  placeholder="Enter nom" formControlName="nom">
            </div>

            <div class="form-group mt-3">
              <label for="prenom">Prenom </label>
              <input type="text" class="form-control" id="prenom"  placeholder="Enter prenom" formControlName="prenom">
            </div>


            <div class="form-group mt-3">
              <label for="cne"> Telephone </label>
              <input type="tel" class="form-control" id="cne"  placeholder="Enter cne" formControlName="telephone">
            </div>

            <div class="form-group mt-3">
              <label for="email"> Email </label>
              <input type="email" class="form-control" id="email"  placeholder="Enter email" formControlName="email">
            </div>

            <div class="form-group mt-3">
              <label for="cne"> Salaire </label>
              <input type="number" class="form-control" id="salaire"  placeholder="Enter cne" formControlName="salaire">
            </div>

          </form>
        </div>
      </div>


      <!-- Modal footer -->
      <div class="modal-footer" style="margin-top: 20px;display: flex;justify-content: center">
        <button type="button" class="btn btn-primary"  style="width: 80%" (click)="save()" data-bs-dismiss="modal">Save</button>
      </div>

    </div>
  </div>
</div>
